import React, { Component } from 'react';
import axios from 'axios'

export default class Form extends Component {
    constructor() {
        super();
        this.state = {
            fullname: "",
            mobile: "",
            email: "",
            pic: "",
            dob: "",
            jobtype: "",
            location: "",
            details: ""

        }
    }
    handleChangefullname = (event) => {
        this.setState({ fullname: event.target.value });
    }
    handleChangemobile = (event) => {
        this.setState({ mobile: event.target.value });
    }
    handleChangeemail = (event) => {
        this.setState({ email: event.target.value });
    }
    handleChangepic = (event) => {
        this.setState({ pic: event.target.value });
    }
    handleChangedob = (event) => {
        this.setState({ dob: event.target.value });
    }
    handleChangejobtype = (event) => {
        this.setState({ jobtype: event.target.value });
    }
    handleChangelocation = (event) => {
        this.setState({ location: event.target.value });
    }
    handlesubmit = (event) => {
        axios.post(`http:localhost:5454/saveDetails`,this.details)
            .then(resp => {
                this.setState({
                    details: resp.data.details

                })
            })
            .catch(err => {
                console.log(err);
            })
    }
    componentDidMount(){
        axios.get(`http://localhost:5454/getAllDetails`)
            .then(resp => {
                this.setState({
                    details: resp.data.details
                })
            })
            .catch(err => {
                console.log(err);
            })
    }

    render() {
        return (
            <React.Fragment>

                <div className='container mt-5 border border-primary'>
                    <div className='row'>
                        <div className="col-lg-12 ms-auto align-center">
                            <p>Registration</p>
                        </div>

                    </div>

                    <div className="row">
                        <form>
                            <div className="col-lg-4 ms-auto">

                                <label for="fname">Fullname:</label><br />
                                <input type="text" id="fname" name="fname" value={this.state.fullname} onChange={this.handleChangefullname}/><br />
                                <label for="mobile">Mobile:</label><br />
                                <input type="tel" id="mobile" name="mobile" value={this.state.mobile} onChange={this.handleChangemobile}/><br />
                                <label for="jobtype">Job Type:</label><br />
                                <input type="text" id="jobtype" name="jobtype" value={this.state.jobtype} onChange={this.handleChangejobtype}/><br />
                                <label for="location">Pref. Location:</label><br />
                                <input type="text" id="location" name="location" value={this.state.location} onChange={this.handleChangelocation}/><br />

                            </div>
                            <div className="col-lg-4 ms-auto">

                                <label for="fname">Profile Pic URL:</label><br />
                                <input type="text" id="profile" name="profile" value={this.state.pic} onChange={this.handleChangepic}/><br />
                                <label for="email">Email:</label><br />
                                <input type="email" id="email" name="email" value={this.state.email} onChange={this.handleChangeemail}/><br />
                                <label for="dob">DOB:</label><br />
                                <input type="text" id="dob" name="dob" value={this.state.dob} onChange={this.handleChangedob}/><br /><br />


                            </div>
                            <button type='submit' onClick={this.handlesubmit}>Add/Update</button>
                        </form>
                    </div>
                </div>

                <div>
                    {/* Display Data */}
                    {JSON.stringify(this.details)}
                </div>

            </React.Fragment >

        )
    }
}
